import React, { useState } from "react";
import Menu from "./menu";

const GalleryReact = () => {
  const [items, setItems] = useState(Menu);
  const [activeCategories, setActiveCategories] = useState([]);

  const handleCategoryClick = (category) => {
    if (activeCategories.includes(category)) {
      setActiveCategories(activeCategories.filter((c) => c !== category));
    } else {
      setActiveCategories([...activeCategories, category]);
    }
  };

  const filteredItems = items.filter((item) => {
    if (activeCategories.length === 0) {
      return true; // Show all items when no categories are selected
    } else {
      // Show items that have at least one of the selected categories
      return item.categories.some((category) => activeCategories.includes(category));
    }
  });

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-light bg-light shadow-sm">
        <span className="navbar-brand mb-0 m-auto h1 text-center">
          Order your favorite dish
        </span>
      </nav>
      <br />

      {/* Category Filter */}
      <div className="container">
        <ul className="nav nav-pills justify-content-center">
          {["All", ...new Set(Menu.flatMap((item) => item.categories))].map((category) => (
            <li className="nav-item" key={category}>
              <a
                className={`nav-link ${activeCategories.includes(category) && "active"}`}
                href="javascript:0;"
                onClick={() => handleCategoryClick(category)}
              >
                {category}
              </a>
            </li>
          ))}
        </ul>
      </div>

      {/* Image Gallery */}
      <div className="container-fluid mt-4">
        <div className="row">
          {filteredItems.map(({ id, name, image, description, price }) => (
            <div className="col-sm-4" key={id}>
              <div className="card mb-3">
                <div className="row no-gutters">
                  <div className="col-md-5">
                    <img className="img-fluid" src={image} alt={name} />
                  </div>
                  <div className="col-md-7">
                    <div className="card-body">
                      <h5 className="card-title mb-0">{name}</h5>
                      <p className="card-text">{description}</p>
                      <div className="d-flex justify-content-between">
                        <div className="p-1">Price: {price}</div>
                        <button type="button" className="btn btn-success p-1">
                          Order Now
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default GalleryReact;
