import React, { useState, useEffect } from "react";
import MenuData from "./menu";

const GalleryReact = () => {
  const [items, setItems] = useState(MenuData);
  const [activeCategories, setActiveCategories] = useState(["All"]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    if (activeCategories.includes("All")) {
      setItems(MenuData);
    } else {
      const filteredItems = MenuData.filter((item) => {
        if (activeCategories.length === 1) {
          return Array.isArray(item.category)
            ? item.category.includes(activeCategories[0])
            : item.category === activeCategories[0];
        } else {
          return (
            Array.isArray(item.category) &&
            activeCategories.every((category) => item.category.includes(category))
          );
        }
      });
      setItems(filteredItems);
    }
  }, [activeCategories]);

  useEffect(() => {
    const filteredItems = MenuData.filter((item) =>
      item.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setItems(filteredItems);
  }, [searchQuery]);

  const handleCategoryClick = (category) => {
    if (category === "All") {
      setActiveCategories(["All"]);
    } else {
      setActiveCategories((prevCategories) => {
        const newCategories = prevCategories.includes(category)
          ? prevCategories.filter((c) => c !== category)
          : [...prevCategories, category];

        return newCategories.includes("All")
          ? newCategories.filter((c) => c !== "All")
          : newCategories;
      });
    }
  };

  const allCategories = [
    "All",
    ...new Set(MenuData.flatMap((item) =>
      Array.isArray(item.category) ? item.category : [item.category]
    )),
  ];

  return (
    <>
      <nav className="navbar navbar-light bg-light shadow-sm">
        <div className="container d-flex flex-column align-items-center">
          <div className="mb-2 text-center">
            <h1 style={{ fontSize: "36px", paddingTop: "30px", color: "#00A4E2" }}>
              Data Visualization Collection
            </h1>
          </div>
          <div className="text-center">
            <p
              className="mb-0"
              style={{ fontSize: "18px", whiteSpace: "pre-line", paddingBottom: "30px" }}
            >
              Exploring alternative ways to visualize data
            </p>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <ul className="nav nav-pills justify-content-center" style={{ paddingTop: "5px", paddingBottom: "5px" }}>
          {allCategories.map((category) => (
            <li className="nav-item" key={category}>
              <a
                className={`nav-link ${activeCategories.includes(category) && "active"}`}
                href="javascript:0;"
                onClick={() => handleCategoryClick(category)}
                style={{
                  backgroundColor: activeCategories.includes(category) ? "#00A4E2" : "#B3EAFF",
                  color: activeCategories.includes(category) ? "white" : "black",
                  marginRight: "5px", // Add spacing between filter boxes
                }}
              >
                {category}
              </a>
            </li>
          ))}
        </ul>
      </div>

      <div className="container mt-2 mb-4" style={{ paddingTop: "15px", paddingBottom: "5px" }}>
        <input
          type="text"
          className="form-control form-control-sm"
          placeholder="Search by Author"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      <div className="container-fluid" style={{ minHeight: "calc(100vh - 180px)" }}>
        <div className="row">
          {items.length === 0 ? (
            <div className="col-12 text-center">
              <h4>No results found.</h4>
            </div>
          ) : (
            items.map(({ id, name, image, description, url }) => (
              <div className="col-sm-6 col-md-4 col-lg-3" key={id}>
                <div
                  className="card mb-3 position-relative"
                  style={{
                    paddingTop: "100%",
                  }}
                  onMouseEnter={() => {
                    const overlay = document.querySelector(`#overlay-${id}`);
                    if (overlay) {
                      overlay.style.visibility = "visible";
                      overlay.style.opacity = 1;
                    }
                  }}
                  onMouseLeave={() => {
                    const overlay = document.querySelector(`#overlay-${id}`);
                    if (overlay) {
                      overlay.style.visibility = "hidden";
                      overlay.style.opacity = 0;
                    }
                  }}
                >
                  <a href={url} target="_blank" rel="noopener noreferrer">
                    <img
                      className="img-fluid"
                      src={process.env.PUBLIC_URL + "/" + image}
                      alt={name}
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                      }}
                    />
                    <div
                      id={`overlay-${id}`}
                      className="image-overlay"
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        color: "white",
                        background: "rgba(0, 0, 0, 0.6)",
                        visibility: "hidden",
                        opacity: 0,
                        transition: "visibility 0s, opacity 0.3s ease",
                        fontSize: "12px", // Adjust font size here
                        padding: "10px", // Adjust padding here
                        textAlign: "center",
                      }}
                    >
                      {description}
                    </div>
                  </a>
                </div>
                <div className="text-center" style={{ marginTop: "-15px", marginBottom: "15px" }}>
                  <h7>{name}</h7>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <footer className="footer mt-auto py-3 bg-light text-center" style={{ marginTop: "40px" }}>
        <div className="container" style={{ paddingTop: "0px", paddingBottom: "0px" }}>
          <p className="m-0">
            This website is for demonstration purposes only. All images and data are provided for
            educational use.
          </p>
        </div>
      </footer>
    </>
  );
};

export default GalleryReact;
