import React, { useState, useEffect } from "react";
import MenuData from "./menu";

const GalleryReact = () => {
  const [items, setItems] = useState(MenuData);
  const [activeCategories, setActiveCategories] = useState(["All"]); // Set "All" as the default active category
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    if (activeCategories.includes("All")) {
      // If "All" is selected, show all items
      setItems(MenuData);
    } else {
      // Filter items based on the selected categories
      const filteredItems = MenuData.filter((item) => {
        if (activeCategories.length === 1) {
          // If only one category is selected, show items that have that category
          return Array.isArray(item.category) ? item.category.includes(activeCategories[0]) : item.category === activeCategories[0];
        } else {
          // If multiple categories are selected, show items that include all selected categories
          const selectedCategoriesSet = new Set(activeCategories);
          return (
            Array.isArray(item.category) &&
            activeCategories.every((category) => item.category.includes(category))
          );
        }
      });
      setItems(filteredItems);
    }
  }, [activeCategories]);

  useEffect(() => {
    // Filter items based on the search query
    const filteredItems = MenuData.filter((item) =>
      item.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setItems(filteredItems);
  }, [searchQuery]);

  const handleCategoryClick = (category) => {
    if (category === "All") {
      // If "All" is clicked, deselect all other categories and select only "All"
      setActiveCategories(["All"]);
    } else {
      // If any other category is clicked, toggle its selection state and deselect "All"
      setActiveCategories((prevCategories) => {
        const newCategories = prevCategories.includes(category)
          ? prevCategories.filter((c) => c !== category)
          : [...prevCategories, category];

        return newCategories.includes("All") ? newCategories.filter((c) => c !== "All") : newCategories;
      });
    }
  };

  const allCategories = ["All", ...new Set(MenuData.flatMap((item) => (Array.isArray(item.category) ? item.category : [item.category])))];

  useEffect(() => {
    // If no category is selected, select "All"
    if (activeCategories.length === 0) {
      setActiveCategories(["All"]);
    }
  }, [activeCategories]);

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-light bg-light shadow-sm">
        <span className="navbar-brand mb-0 m-auto h1 text-center">Order your favorite dish</span>
      </nav>

      {/* Add space on top */}
      <div className="container mt-4">
        {/* Category Filter */}
        <ul className="nav nav-pills justify-content-center">
          {allCategories.map((category) => (
            <li className="nav-item" key={category}>
              <a
                className={`nav-link ${activeCategories.includes(category) && "active"}`}
                href="javascript:0;"
                onClick={() => handleCategoryClick(category)}
              >
                {category}
              </a>
            </li>
          ))}
        </ul>
      </div>

      {/* Smaller Search Bar */}
      <div className="container mt-2 mb-4">
        <input
          type="text"
          className="form-control form-control-sm"
          placeholder="Search by name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* Image Gallery */}
      <div className="container-fluid">
        <div className="row">
          {items.map(({ id, name, image, description }) => (
            <div className="col-sm-4" key={id}>
              <div className="card mb-3">
                <div className="row no-gutters">
                  <div className="col-md-5">
                    <img className="img-fluid" src={image} alt={name} />
                  </div>
                  <div className="col-md-7">
                    <div className="card-body">
                      <h5 className="card-title mb-0">{name}</h5>
                      <p className="card-text">{description}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default GalleryReact;
