import React, { useState, useEffect } from "react";
import Menu from "./menu";

const GalleryReact = () => {
  const [items, setItems] = useState(Menu);
  const [activeCategories, setActiveCategories] = useState(["All"]); // Set "All" as the default active category

  useEffect(() => {
    if (activeCategories.includes("All")) {
      // If "All" is selected, show all items
      setItems(Menu);
    } else {
      // Filter items based on the selected categories
      const filteredItems = Menu.filter((item) => {
        if (Array.isArray(item.category)) {
          return item.category.some((category) => activeCategories.includes(category));
        } else {
          return activeCategories.includes(item.category);
        }
      });
      setItems(filteredItems);
    }
  }, [activeCategories]);

  const handleCategoryClick = (category) => {
    if (category === "All") {
      // If "All" is clicked, deselect all other categories and select only "All"
      setActiveCategories(["All"]);
    } else {
      // If any other category is clicked, toggle its selection state
      setActiveCategories((prevCategories) =>
        prevCategories.includes(category)
          ? prevCategories.filter((c) => c !== category)
          : [...prevCategories, category]
      );
    }
  };

  const allCategories = ["All", ...new Set(Menu.flatMap((item) => (Array.isArray(item.category) ? item.category : [item.category])))];

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-light bg-light shadow-sm">
        <span className="navbar-brand mb-0 m-auto h1 text-center">
          Order your favorite dish
        </span>
      </nav>
      <br />

      {/* Category Filter */}
      <div className="container">
        <ul className="nav nav-pills justify-content-center">
          {allCategories.map((category) => (
            <li className="nav-item" key={category}>
              <a
                className={`nav-link ${activeCategories.includes(category) && "active"}`}
                href="javascript:0;"
                onClick={() => handleCategoryClick(category)}
              >
                {category}
              </a>
            </li>
          ))}
        </ul>
      </div>

      {/* Image Gallery */}
      <div className="container-fluid mt-4">
        <div className="row">
          {items.map(({ id, name, image, description, price }) => (
            <div className="col-sm-4" key={id}>
              <div className="card mb-3">
                <div className="row no-gutters">
                  <div className="col-md-5">
                    <img className="img-fluid" src={image} alt={name} />
                  </div>
                  <div className="col-md-7">
                    <div className="card-body">
                      <h5 className="card-title mb-0">{name}</h5>
                      <p className="card-text">{description}</p>
                      <div className="d-flex justify-content-between">
                        <div className="p-1">Price: {price}</div>
                        <button type="button" className="btn btn-success p-1">
                          Order Now
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default GalleryReact;
