import React, { useState, useEffect, useRef } from "react";
import MenuData from "./menu";

const GalleryReact = () => {
  const [items, setItems] = useState(MenuData);
  const [activeCategories, setActiveCategories] = useState(["All"]);
  const [searchQuery, setSearchQuery] = useState("");
  const [isFetching, setIsFetching] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const containerRef = useRef(null);

  useEffect(() => {
    if (!isFetching) return;

    // Simulate an API call or fetch more data from your server
    // In this example, we'll just add more data from MenuData
    setTimeout(() => {
      setItems((prevItems) => [
        ...prevItems,
        ...MenuData.slice(prevItems.length, prevItems.length + 4), // Add 4 more items
      ]);
      setIsFetching(false);
    }, 1000); // Simulate a delay

    // Clean up by resetting isFetching and pageNumber after the data is loaded
    return () => {
      setIsFetching(false);
      setPageNumber((prevPageNumber) => prevPageNumber + 1);
    };
  }, [isFetching]);

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: "20px",
      threshold: 1.0,
    };

    const observer = new IntersectionObserver(handleObserver, options);
    if (containerRef.current) {
      observer.observe(containerRef.current);
    }

    return () => {
      if (containerRef.current) {
        observer.unobserve(containerRef.current);
      }
    };
  }, []);

  const handleObserver = (entities) => {
    const target = entities[0];
    if (target.isIntersecting) {
      setIsFetching(true);
    }
  };

  useEffect(() => {
    if (activeCategories.includes("All")) {
      setItems(MenuData);
    } else {
      const filteredItems = MenuData.filter((item) => {
        if (activeCategories.length === 1) {
          return Array.isArray(item.category)
            ? item.category.includes(activeCategories[0])
            : item.category === activeCategories[0];
        } else {
          return (
            Array.isArray(item.category) &&
            activeCategories.every((category) => item.category.includes(category))
          );
        }
      });
      setItems(filteredItems);
    }
  }, [activeCategories]);

  useEffect(() => {
    const filteredItems = MenuData.filter((item) =>
      item.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setItems(filteredItems);
  }, [searchQuery]);

  const handleCategoryClick = (category) => {
    if (category === "All") {
      setActiveCategories(["All"]);
    } else {
      setActiveCategories((prevCategories) => {
        const newCategories = prevCategories.includes(category)
          ? prevCategories.filter((c) => c !== category)
          : [...prevCategories, category];

        return newCategories.includes("All")
          ? newCategories.filter((c) => c !== "All")
          : newCategories;
      });
    }
  };

  const allCategories = [
    "All",
    ...new Set(MenuData.flatMap((item) =>
      Array.isArray(item.category) ? item.category : [item.category]
    )),
  ];

  return (
    <>
      <nav className="navbar navbar-light bg-light shadow-sm">
        <span className="navbar-brand mb-0 m-auto h1 text-center">Order your favorite dish</span>
      </nav>

      <div className="container mt-4">
        <ul className="nav nav-pills justify-content-center">
          {allCategories.map((category) => (
            <li className="nav-item" key={category}>
              <a
                className={`nav-link ${activeCategories.includes(category) && "active"}`}
                href="javascript:0;"
                onClick={() => handleCategoryClick(category)}
              >
                {category}
              </a>
            </li>
          ))}
        </ul>
      </div>

      <div className="container mt-2 mb-4">
        <input
          type="text"
          className="form-control form-control-sm"
          placeholder="Search by name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      <div className="container-fluid">
        <div className="row">
          {items.map(({ id, name, image, description, url }) => (
            <div className="col-sm-6 col-md-4 col-lg-3" key={id}>
              <div
                className="card mb-3"
                style={{
                  position: "relative",
                  width: "100%",
                  paddingTop: "100%",
                }}
              >
                <a href={url} target="_blank" rel="noopener noreferrer">
                  <div
                    className="image-overlay"
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      width: "100%",
                      height: "100%",
                      display: "flex",
                      flexDirection: "column",
                      justifyContent: "center",
                      alignItems: "center",
                      color: "white",
                      visibility: "hidden",
                      opacity: 0,
                      background: "rgba(0, 0, 0, 0.6)",
                      transition: "visibility 0s, opacity 0.3s ease",
                    }}
                    onMouseEnter={() => {
                      const overlay = document.querySelector(`#overlay-${id}`);
                      if (overlay) {
                        overlay.style.visibility = "visible";
                        overlay.style.opacity = 1;
                      }
                    }}
                    onMouseLeave={() => {
                      const overlay = document.querySelector(`#overlay-${id}`);
                      if (overlay) {
                        overlay.style.visibility = "hidden";
                        overlay.style.opacity = 0;
                      }
                    }}
                  >
                    <h5
                      className="mb-0"
                      style={{
                        fontSize: "14px",
                        margin: "0 10px",
                        textAlign: "center",
                      }}
                    >
                      {description}
                    </h5>
                  </div>
                  <img
                    className="img-fluid"
                    src={process.env.PUBLIC_URL + "/" + image}
                    alt={name}
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                    }}
                  />
                  <div
                    id={`overlay-${id}`}
                    className="image-overlay"
                    style={{
                      position: "absolute",
                      top: "70%",
                      width: "100%",
                      height: "30%",
                      display: "flex",
                      flexDirection: "column",
                      justifyContent: "center",
                      alignItems: "center",
                      color: "white",
                      background: "rgba(0, 0, 0, 0.6)",
                    }}
                  >
                    <h5 className="mb-0">{name}</h5>
                  </div>
                </a>
              </div>
            </div>
          ))}
        </div>
        <div ref={containerRef} style={{ height: "20px" }}></div>
      </div>
    </>
  );
};

export default GalleryReact;
